import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View ,ScrollView, TextInput} from 'react-native';
import MeuComponente from './components/MeuComponente';
import Button from './components/Button'
import MOCK_DATA from './MOCK_DATA.json'


export default function App() {
  const[searchWord, setSearchWord] = useState('')
  const signIn = () => {
    alert('Cliquei');
  }

  return (
    <View style={styles.container}>
      <StatusBar style="light" />
      <Text style={styles.title}>Cat√°logo de Televisores </Text>
      <TextInput style={styles.marca} placeholder='Pesquisar por marcas' onChangeText={setSearchWord}/>

      <ScrollView>
        {MOCK_DATA.filter((val) =>{
          if(setSearchWord === ""){
            return val
          }else if(val.first_name.toLowerCase().includes(searchWord)){
            return val
          }

        }).map((item, index) => (
          <Text key={index}>{item.first_name}  </Text>
        ))}
        <MeuComponente/>
        <Button labelButton="LCD" onpress={signIn} />
        <Button labelButton="LED" onpress={signIn} />
        <Button labelButton="PlasMA" onpress={signIn} />


      </ScrollView>
    </View> 
  ); 
}
